import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertContactSubmissionSchema, insertAnalyticsEventSchema } from "@shared/schema";
import { contactFormRateLimit, analyticsRateLimit } from "./middleware/rate-limit";
import { emailService } from "./services/email";

export async function registerRoutes(app: Express): Promise<Server> {
  // Contact form submission
  app.post("/api/contact", contactFormRateLimit.middleware(), async (req, res) => {
    try {
      // Honeypot check
      if (req.body.website) {
        return res.status(400).json({ 
          success: false, 
          message: "Invalid submission" 
        });
      }

      const validatedData = insertContactSubmissionSchema.parse(req.body);

      const submission = await storage.createContactSubmission({
        ...validatedData,
        ipAddress: req.ip,
        userAgent: req.get("User-Agent"),
      });

      // Send email notification
      const emailSent = await emailService.sendContactNotification({
        ...validatedData,
        company: validatedData.company || undefined,
        utmSource: validatedData.utmSource || undefined
      });

      if (!emailSent) {
        console.error("Failed to send email notification for contact submission:", submission.id);
      }

      res.json({ 
        success: true, 
        message: "Thank you for your message! I'll get back to you soon.",
        submissionId: submission.id
      });
    } catch (error) {
      console.error("Contact form error:", error);

      if (error instanceof Error && "issues" in error) {
        return res.status(400).json({ 
          success: false, 
          message: "Please check your input and try again.",
          errors: error.issues 
        });
      }

      res.status(500).json({ 
        success: false, 
        message: "Something went wrong. Please try again later." 
      });
    }
  });

  // Analytics event tracking
  app.post("/api/analytics", analyticsRateLimit.middleware(), async (req, res) => {
    try {
      const validatedData = insertAnalyticsEventSchema.parse(req.body);

      await storage.createAnalyticsEvent({
        ...validatedData,
        ipAddress: req.ip,
        userAgent: req.get("User-Agent"),
      });

      res.json({ success: true });
    } catch (error) {
      console.error("Analytics error:", error);
      res.status(400).json({ success: false });
    }
  });

  // Resume download tracking
  app.get("/api/resume/download", async (req, res) => {
    try {
      // Track download event
      await storage.createAnalyticsEvent({
        event: "resume_download",
        properties: { 
          source: req.get("Referer") || "direct",
          userAgent: req.get("User-Agent") 
        },
        sessionId: req.get("X-Session-ID"),
        ipAddress: req.ip,
        userAgent: req.get("User-Agent"),
      });

      // In a real implementation, you would serve the actual PDF file
      // For now, we'll return a success response
      res.json({ 
        success: true, 
        message: "Resume download tracked",
        downloadUrl: "/jacob-darling-resume.pdf" // This would be a real file URL
      });
    } catch (error) {
      console.error("Resume download error:", error);
      res.status(500).json({ success: false });
    }
  });

  // Health check
  app.get("/api/health", (req, res) => {
    res.json({ status: "ok", timestamp: new Date().toISOString() });
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { type User, type InsertUser, type ContactSubmission, type InsertContactSubmission, type AnalyticsEvent, type InsertAnalyticsEvent } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  createContactSubmission(submission: InsertContactSubmission & { ipAddress?: string; userAgent?: string }): Promise<ContactSubmission>;
  createAnalyticsEvent(event: InsertAnalyticsEvent & { ipAddress?: string; userAgent?: string }): Promise<AnalyticsEvent>;
  getContactSubmissions(): Promise<ContactSubmission[]>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private contactSubmissions: Map<string, ContactSubmission>;
  private analyticsEvents: Map<string, AnalyticsEvent>;

  constructor() {
    this.users = new Map();
    this.contactSubmissions = new Map();
    this.analyticsEvents = new Map();
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  async createContactSubmission(submission: InsertContactSubmission & { ipAddress?: string; userAgent?: string }): Promise<ContactSubmission> {
    const id = randomUUID();
    const contactSubmission: ContactSubmission = {
      ...submission,
      company: submission.company || null,
      utmSource: submission.utmSource || null,
      ipAddress: submission.ipAddress || null,
      userAgent: submission.userAgent || null,
      id,
      createdAt: new Date(),
    };
    this.contactSubmissions.set(id, contactSubmission);
    return contactSubmission;
  }

  async createAnalyticsEvent(event: InsertAnalyticsEvent & { ipAddress?: string; userAgent?: string }): Promise<AnalyticsEvent> {
    const id = randomUUID();
    const analyticsEvent: AnalyticsEvent = {
      ...event,
      properties: event.properties || null,
      sessionId: event.sessionId || null,
      ipAddress: event.ipAddress || null,
      userAgent: event.userAgent || null,
      id,
      createdAt: new Date(),
    };
    this.analyticsEvents.set(id, analyticsEvent);
    return analyticsEvent;
  }

  async getContactSubmissions(): Promise<ContactSubmission[]> {
    return Array.from(this.contactSubmissions.values()).sort(
      (a, b) => (b.createdAt?.getTime() || 0) - (a.createdAt?.getTime() || 0)
    );
  }
}

export const storage = new MemStorage();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, timestamp, json, boolean } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const users = pgTable("users", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  username: text("username").notNull().unique(),
  password: text("password").notNull(),
});

export const contactSubmissions = pgTable("contact_submissions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  email: text("email").notNull(),
  company: text("company"),
  message: text("message").notNull(),
  utmSource: text("utm_source"),
  ipAddress: text("ip_address"),
  userAgent: text("user_agent"),
  createdAt: timestamp("created_at").defaultNow(),
});

export const analyticsEvents = pgTable("analytics_events", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  event: text("event").notNull(),
  properties: json("properties"),
  sessionId: text("session_id"),
  ipAddress: text("ip_address"),
  userAgent: text("user_agent"),
  createdAt: timestamp("created_at").defaultNow(),
});

export const insertUserSchema = createInsertSchema(users).pick({
  username: true,
  password: true,
});

export const insertContactSubmissionSchema = createInsertSchema(contactSubmissions).pick({
  name: true,
  email: true,
  company: true,
  message: true,
  utmSource: true,
}).extend({
  email: z.string().email("Invalid email address"),
  name: z.string().min(1, "Name is required"),
  message: z.string().min(10, "Message must be at least 10 characters"),
});

export const insertAnalyticsEventSchema = createInsertSchema(analyticsEvents).pick({
  event: true,
  properties: true,
  sessionId: true,
});

export type InsertUser = z.infer<typeof insertUserSchema>;
export type User = typeof users.$inferSelect;
export type InsertContactSubmission = z.infer<typeof insertContactSubmissionSchema>;
export type ContactSubmission = typeof contactSubmissions.$inferSelect;
export type InsertAnalyticsEvent = z.infer<typeof insertAnalyticsEventSchema>;
export type AnalyticsEvent = typeof analyticsEvents.$inferSelect;

client/src/App.tsx.content:
import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { ThemeProvider } from "@/components/layout/theme-provider";
import NotFound from "@/pages/not-found";
import Home from "@/pages/home";
import Work from "@/pages/work";
import CaseStudy from "@/pages/work/[slug]";
import Toolbox from "@/pages/toolbox";
import About from "@/pages/about";
import Contact from "@/pages/contact";
import ContactSuccess from "@/pages/contact-success";

function Router() {
  return (
    <Switch>
      <Route path="/" component={Home} />
      <Route path="/work" component={Work} />
      <Route path="/work/:slug" component={CaseStudy} />
      <Route path="/toolbox" component={Toolbox} />
      <Route path="/about" component={About} />
      <Route path="/contact" component={Contact} />
      <Route path="/contact/success" component={ContactSuccess} />
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  return (
    <QueryClientProvider client={queryClient}>
      <ThemeProvider>
        <TooltipProvider>
          <Toaster />
          <Router />
        </TooltipProvider>
      </ThemeProvider>
    </QueryClientProvider>
  );
}

export default App;

