@import url('https://fonts.googleapis.com/css2?family=Space+Grotesk:wght@300;400;500;600;700&family=Inter:wght@300;400;500;600;700&family=JetBrains+Mono:wght@400;500&display=swap');

@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  /* Typography Scale (Fluid) */
  --font-display: clamp(2.2rem, 2.6vw + 1rem, 3rem);
  --font-h1: clamp(1.8rem, 1.6vw + 1rem, 2.4rem);
  --font-h2: clamp(1.4rem, 1.2vw + 0.9rem, 1.8rem);
  --font-body: clamp(1rem, 0.35vw + 0.9rem, 1.1rem);

  /* Radius Tokens */
  --r-card: 1.25rem;
  --r-chip: 999px;
  --radius: 0.75rem;

  /* Shadow Tokens */
  --sh-card: 0 8px 30px rgba(0,0,0,0.06);
  --sh-hover: 0 12px 40px rgba(0,0,0,0.10);

  /* Spacing Tokens */
  --space-section: clamp(2.5rem, 6vw, 5rem);

  /* Motion Tokens */
  --dur-fast: 160ms;
  --dur-med: 260ms;
  --easing: cubic-bezier(.2,.7,.2,1);

  /* Color System */
  --background: hsl(0, 0%, 99%);
  --surface: hsl(0, 0%, 100%);
  --foreground: hsl(224, 71%, 4%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(224, 71%, 4%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(224, 71%, 4%);
  --primary: hsl(234, 89%, 74%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(220, 14%, 96%);
  --secondary-foreground: hsl(224, 71%, 4%);
  --muted: hsl(220, 13%, 91%);
  --muted-foreground: hsl(220, 8.9%, 46%);
  --accent: hsl(234, 89%, 74%);
  --accent-foreground: hsl(0, 0%, 100%);
  --success: hsl(142, 76%, 36%);
  --warning: hsl(32, 95%, 44%);
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(220, 13%, 91%);
  --input: hsl(220, 13%, 91%);
  --ring: hsl(234, 89%, 74%);
  --chart-1: hsl(12, 76%, 61%);
  --chart-2: hsl(173, 58%, 39%);
  --chart-3: hsl(197, 37%, 24%);
  --chart-4: hsl(43, 74%, 66%);
  --chart-5: hsl(27, 87%, 67%);

  /* Fonts */
  --font-family-display: 'Space Grotesk', sans-serif;
  --font-family-sans: 'Inter', system-ui, sans-serif;
  --font-family-mono: 'JetBrains Mono', ui-monospace, monospace;
}

.dark {
  /* Dark Color System */
  --background: hsl(224, 71%, 4%);
  --surface: hsl(224, 45%, 7%);
  --foreground: hsl(213, 31%, 91%);
  --card: hsl(224, 45%, 7%);
  --card-foreground: hsl(213, 31%, 91%);
  --popover: hsl(224, 45%, 7%);
  --popover-foreground: hsl(213, 31%, 91%);
  --primary: hsl(234, 89%, 74%);
  --primary-foreground: hsl(224, 71%, 4%);
  --secondary: hsl(215, 27%, 17%);
  --secondary-foreground: hsl(213, 31%, 91%);
  --muted: hsl(215, 27%, 17%);
  --muted-foreground: hsl(217, 10%, 64%);
  --accent: hsl(234, 89%, 74%);
  --accent-foreground: hsl(224, 71%, 4%);
  --success: hsl(142, 76%, 36%);
  --warning: hsl(32, 95%, 44%);
  --destructive: hsl(0, 62%, 30%);
  --destructive-foreground: hsl(213, 31%, 91%);
  --border: hsl(215, 27%, 17%);
  --input: hsl(215, 27%, 17%);
  --ring: hsl(234, 89%, 74%);

  /* Dark mode shadows */
  --sh-card: 0 8px 30px rgba(0,0,0,0.4);
  --sh-hover: 0 12px 40px rgba(0,0,0,0.6);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground antialiased;
    font-family: var(--font-family-sans);
    font-size: var(--font-body);
    line-height: 1.6;
  }

  html {
    scroll-behavior: smooth;
  }
}

@layer components {
  .gradient-text {
    background: linear-gradient(135deg, hsl(var(--primary)) 0%, hsl(var(--chart-1)) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .glass-effect {
    background: rgba(255, 255, 255, 0.1);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.2);
