          {value}
        </span>
      </div>
      {baseline && (
        <span className="text-xs opacity-60 ml-auto">
          vs {baseline}
        </span>
      )}
    </div>
  );
}

export function StatChipGroup({ 
  children, 
  className 
}: { 
  children: React.ReactNode; 
  className?: string; 
}) {
  return (
    <div 
      className={cn("flex flex-wrap gap-3", className)}
      data-testid="stat-chip-group"
    >
      {children}
    </div>
  );
}

